  constructor(name, version) {
    this.name = name;
    this.version = version;
  }

  log() {
    console.log('parent');
  }
}

class PersonPlus extends Person {
  constructor(name, version) {
    super(name, version);
  }
  log() {
    super.log();
    console.log('child');
  }
}

const plusTom = new PersonPlus('Tom', '1.0.0');
console.log(plusTom.version);
plusTom.log();

