function debounce(func, delay) {
  let timer = null;
  return function() {
    const context = this;
    const args = arguments;
    clearTimeout(timer);
    timer = setTimeout(function() {
      func.apply(context, args);
    }, delay);
  };
}

const debounceFn = debounce(function() {
  console.log('debounce');
}, 1000);
debounceFn();

function throttle(func, delay) {
  let timer = null;
  return function() {
    const context = this;
    const args = arguments;
    if (!timer) {
      timer = setTimeout(function() {
        func.apply(context, args);
        timer = null;
      }, delay);
    }
  };
}

const throttleFn = throttle(function() {
  console.log('throttle');
}, 1000);
throttleFn();

const debounce = (func, delay) => {
        let timeoutId;
        return (...args) => {
          if (timeoutId) {
            clearTimeout(timeoutId);
          }
          timeoutId = setTimeout(() => {
            func.apply(null, args);
          }, delay);
        };
      };

function debounceImmediate(fn, wait) {
  let timer = null; // 定义计时器，初始值为null
  return function (...args) { // 返回一个函数，利用闭包来保存timer变量
    if (timer) { // 如果timer存在，则说明函数还在等待执行
      clearTimeout(timer); // 清除计时器
    } else { // 如果timer不存在，则说明可以立即执行函数
      fn.apply(this, args); // 执行函数，并将上下文和参数传递进去
    }
    timer = setTimeout(() => { // 设置计时器，在规定时间后将timer设为null
      timer = null;
    }, wait);
  };
}

function debounce(fn, wait) {
  let timer = null;
  return function (...args) {
    const context = this;
    timer && clearTimeout(timer);
    timer = setTimeout(() => {
      fn.apply(context, args);
    }, wait);
  };
}

function throttleImmediate(fn, wait) {
  let timer = null; // 定义计时器，初始值为null
  return function (...args) { // 返回一个函数，利用闭包来保存timer变量
    if (!timer) { // 如果timer不存在，则说明可以立即执行函数
      fn.apply(this, args); // 执行函数，并将上下文和参数传递进去
      timer = setTimeout(() => { // 设置计时器，在规定时间后将timer设为null
        timer = null;
      }, wait);
    }
  };
}

function throttle(fn, wait) {
  let timer = null;
  return function (...args) {
    if (!timer) {
      const context = this;
      fn.apply(context, args);
      timer = setTimeout(() => {
        timer = null;
      }, wait);
    }
  };
}

