import React, { Component } from 'react';

class MyComponent extends Component {
  submitHandler = (event) => {
    event.preventDefault();
    console.log('Form submitted');
  };

  render() {
    return (
      <form onSubmit={this.submitHandler}>
        <button type="submit">Submit</button>
      </form>
    );
  }
}

