class MathUtils {
  static add(a, b) {
    return a + b;
  }
}

// es5
function MathUtils() {}

// static method
MathUtils.add = function () {
	return a + b;
}

// test
const sum = MathUtils.add(1, 2);
console.log(sum); // 3

