const xhr = new XMLHttpRequest();

// 设置请求 URL 和方法
xhr.open('GET', 'path/to/data.json');

// 处理响应数据
xhr.onload = function() {
  if (xhr.status === 200) {
    const data = JSON.parse(xhr.responseText);
    console.log(data);
  } else {
    console.error('Request failed. Status code:', xhr.status);
  }
};

// 发送请求
xhr.send();

