Cloning the template repository...
`);

const result = spawn.sync(
  "git",
  [
    "clone",
    "--depth",
    "1",
    "-b",
    templateBranch,
    "https://github.com/oeyoews/neotw.git",
    appPath,
  ],
  { stdio: "inherit" }
);

if (result.status !== 0) {
  console.error(`
    Failed to clone the template repository. Please check your network connection and try again.
  `);
  process.exit(1);
}

process.chdir(appPath);

if (templateBranch === "main") {
  console.log(`
Installing dependencies. This might take a couple of minutes...
`);

  const result2 = spawn.sync("npm", ["install"], { stdio: "inherit" });

  if (result2.status !== 0) {
    console.error(`
    Failed to install dependencies. Please run the following command manually:
    npm install
  `);
    process.exit(1);
  }
}

console.log(`
Success! Created ${appName} at ${appPath}
Inside that directory, you can run several commands:

  npm start
    Starts the development server.

We suggest that you begin by typing:

  cd ${appName}
  npm start

Happy hacking!
`);

function debounce(func, delay) {
  let timer = null;
  return function() {
    const context = this;
    const args = arguments;
    clearTimeout(timer);
    timer = setTimeout(function() {
      func.apply(context, args);
    }, delay);
  };
}

const debounceFn = debounce(function() {
  console.log('debounce');
}, 1000);
debounceFn();

function throttle(func, delay) {
  let timer = null;
  return function() {
    const context = this;
    const args = arguments;
    if (!timer) {
      timer = setTimeout(function() {
        func.apply(context, args);
        timer = null;
      }, delay);
    }
  };
}

const throttleFn = throttle(function() {
  console.log('throttle');
}, 1000);
throttleFn();

const debounce = (func, delay) => {
        let timeoutId;
        return (...args) => {
          if (timeoutId) {
            clearTimeout(timeoutId);
          }
          timeoutId = setTimeout(() => {
            func.apply(null, args);
          }, delay);
        };
      };

