class Logger {
  constructor() {
    this.log = [];
  }

  add(message) {
    this.log.push(message);
  }

  print() {
    console.log(this.log.join('\n'));
  }

  static getInstance() {
    if (!Logger.instance) {
      Logger.instance = new Logger();
    }
    return Logger.instance;
  }
}

const logger = Logger.getInstance();
logger.add('Hello');
logger.add('World');
logger.print();

const Logger = (function () {
  let instance;

  function createInstance() {
    const log = [];

    function add(message) {
      log.push(message);
    }

    function print() {
      console.log(log.join('\n'));
    }

    return {
      add,
      print,
    };
  }

  return {
    getInstance: function () {
      if (!instance) {
        instance = createInstance();
      }
      return instance;
    },
  };
})();

const logger = Logger.getInstance();
logger.add('Hello');
logger.add('World');
logger.print();

