function MyComponent({ id }) {
  const [data, setData] = useState(null);

  // This effect runs once, when the component mounts
  useEffect(() => {
    // Initialize data
    setData(fetchData(id));
  }, [id]);

  // This effect runs whenever `id` changes
  useEffect(() => {
    // Update data
    setData(fetchData(id));
  }, [id]);

  // Render the component
  return <div>{data}</div>;
}

