// 监听链接点击事件
document.addEventListener('click', function(event) {
  // 如果被点击的元素是链接
  if (event.target.tagName === 'A') {
    event.preventDefault(); // 阻止默认链接行为

    // 使用 AJAX 加载新页面 HTML
    fetch(event.target.href, { headers: { 'X-PJAX': true } })
      .then(response => response.text())
      .then(html => {
        // 创建一个新的虚拟 DOM 对象
        const tmp = document.createElement('div');
        tmp.innerHTML = html;

        // 获取需要更新的页面内容
        const content = tmp.querySelector('#content');

        // 更新页面内容
        document.querySelector('#content').innerHTML = content.innerHTML;

        // 更新浏览器历史记录
        history.pushState({}, null, event.target.href);
      });
  }
});

// 监听 popstate 事件（比如点击浏览器的前进或后退按钮）
window.addEventListener('popstate', function(event) {
  // 使用 AJAX 加载当前页面 HTML
  fetch(location.href, { headers: { 'X-PJAX': true } })
    .then(response => response.text())
    .then(html => {
      // 创建一个新的虚拟 DOM 对象
      const tmp = document.createElement('div');
      tmp.innerHTML = html;

      // 获取需要更新的页面内容
      const content = tmp.querySelector('#content');

      // 更新页面内容
      document.querySelector('#content').innerHTML = content.innerHTML;
    });
});

function asyncTask() {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      const randomNumber = Math.random();
      if (randomNumber > 0.5) {
        resolve(randomNumber);
      } else {
        reject(new Error("Random number is too small"));
      }
    }, 1000);
  });
}

asyncTask()
  .then(result => {
    console.log(`Async task successful, result: ${result}`);
  })
  .catch(error => {
    console.error(`Async task failed, error: ${error}`);
  });

