// 定义一个父类
class Animal {
  constructor(name) {
    this.name = name;
  }

  sayName() {
    console.log('My name is ' + this.name);
  }
}

// 定义一个子类
class Dog extends Animal {
  constructor(name, breed) {
    super(name); // 调用父类构造函数
    this.breed = breed;
  }

  bark() {
    console.log('Woof!');
  }
}

// 创建一个实例
var myDog = new Dog('Fido', 'Labrador');

// 调用父类的方法
myDog.sayName(); // 输出 "My name is Fido"

// 调用子类的方法
myDog.bark(); // 输出 "Woof!"
