"},"$:/plugins/oeyoews/neotw-recent-cards/widget.js":{"title":"$:/plugins/oeyoews/neotw-recent-cards/widget.js","text":"/*\
title: $:/plugins/oeyoews/neotw-recent-cards/widget.js
type: application/javascript
module-type: widget

neotw-recent-cards widget

\*/
(function () {
  /*jslint node: true, browser: true */

  /*global $tw: false */
  'use strict';

  const Widget = require('$:/core/modules/widgets/widget.js').widget;

  class cardsWidget extends Widget {
    constructor(parseTreeNode, options) {
      super(parseTreeNode, options);
      this.recentTiddlers = $tw.wiki.filterTiddlers(
        '[!is[system]!has[draft.of]!sort[created]limit[9]]',
      );
    }

    render(parent, nextSibling) {
      if (!$tw.browser) return;

      this.parentDomNode = parent;
      this.computeAttributes();
      this.execute();

      const wiki = $tw.wiki;

      const recentTiddlers = this.recentTiddlers;

      const data = recentTiddlers.map((tiddler) => {
        const { fields } = wiki.getTiddler(tiddler);
        const cover =
          fields['page-cover'] ||
          `https://source.unsplash.com/random/1920x1080?fm=blurhash&${fields.title}`;
        return {
          title: fields.title,
          cover,
        };
      });

      const container = document.createElement('div');
      container.classList.add(
        'grid',
        'grid-cols-1',
        'sm:grid-cols-2',
        'md:grid-cols-3',
        'gap-4',
      );

      // 必须使用箭头函数
      // 会触发notebook的closing
      const navigate = (title) => {
        this.parentWidget.dispatchEvent({
          type: 'tm-navigate',
          param: title,
          navigateTo: title,
        });
        return false;
      };

      function createCard(title, cover) {
        const item = document.createElement('div');
        item.classList.add(
          'flex',
          'flex-col',
          'items-start',
          'group',
          'relative',
        );
        const h3 = document.createElement('h3');
        h3.classList.add(
          'font-normal',
          'text-sm',
          'truncate',
          'm-0',
          'absolute',
          'bottom-0',
          'left-0',
          'w-full',
          'bg-black',
          'backdrop-blur',
          'bg-opacity-50',
          'text-white',
          'p-2',
          'rounded-md',
          'scale-0',
          'ease-in-out',
          'transition',
          'group-hover:scale-105',
        );
        h3.textContent = title;
        item.addEventListener('click', () => navigate(title));
        const img = document.createElement('img');
        img.title = '点击查看';
        img.classList.add(
          'object-cover',
          'w-full',
          'h-full',
          'rounded-md',
          'group-hover:scale-105',
          'transition',
          'duration-400',
          'ease-in-out',
          'cursor-pointer',
        );
        img.src = cover;
        item.appendChild(img);
        item.appendChild(h3);
        return item;
      }

      data.forEach(({ title, cover }) => {
        container.appendChild(createCard(title, cover));
      });

      parent.insertBefore(container, nextSibling);
      this.domNodes.push(container);
    }

    // 不要进行实时更新
    /* refresh() {
      const recentTiddlers = $tw.wiki.filterTiddlers(
        '[!is[system]!has[draft.of]!sort[created]limit[9]]',
      );
      if (recentTiddlers[0] !== this.recentTiddlers[0]) {
        this.refreshSelf();
        console.log('updated');
      }
    } */
  }

  exports.cards = cardsWidget;
})();
","type":"application/javascript","module-type":"widget"}}}
