window.onload = function() {
    // 阻止双击放大
    var lastTouchEnd = 0;
    document.addEventListener('touchstart', function(event) {
        if (event.touches.length > 1) {
            event.preventDefault();
        }
    });
    document.addEventListener('touchend', function(event) {
        var now = (new Date()).getTime();
        if (now - lastTouchEnd <= 300) {
            event.preventDefault();
        }
        lastTouchEnd = now;
    }, false);

    // 阻止双指放大
    document.addEventListener('gesturestart', function(event) {
        event.preventDefault();
    });
}
</script>
"},"$:/plugins/oeyoews/neotw/style/scrollbar":{"title":"$:/plugins/oeyoews/neotw/style/scrollbar","tags":"$:/tags/Stylesheet","text":"\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline html

html {
  --scrollbarBG: <<color page-background>>;
  --thumbBG: <<color sidebar-tiddler-link-foreground>>;
	/*other candidate colors: foreground, sidebar-tiddler-link-foreground */
}

/* only for Twilight palette use <<color sidebar-muted-foreground>> */

/* Works on Firefox */
* {
  scrollbar-width: thin;
}
/* for Chrome, Edge */
::-webkit-scrollbar,
  scrollbar-width {
  width: 7px;
  height:5px;
}

::-webkit-scrollbar:hover,
  scrollbar-width {
  width: 7.5px;
}

::-webkit-scrollbar-track {
  background: var(--scrollbarBG);
}

/* background-color: var(--thumbBG) ;
	background: linear-gradient(to top, #00f260, #0575e6); */
::-webkit-scrollbar-thumb {
  border-radius: 3px;
  border: 0px solid var(--scrollbarBG);
    background: linear-gradient(to top, #12c2e9, #c471ed, #f64f59); /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */;
}

::-webkit-scrollbar-thumb:hover {
	background: linear-gradient(45deg, #00f260, #3AC9E1);
}
"},"$:/plugins/oeyoews/neotw/style/shield":{"title":"$:/plugins/oeyoews/neotw/style/shield","tags":"$:/tags/Stylesheet","text":"\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline
.shield {
  font-weight: normal;
  display: inline-block;
  color: white;
  font-size: .8em;
  margin: 5px;
}

.shield:hover {
  text-decoration: none;
}

.shield .label {
  background: #555;
	border-radius: 3px 0 0 3px;
  padding: 3px 6px;
}

.shield .status {
  background: #40ba12;
	border-radius: 0 3px 3px 0;
  padding: 3px 6px;
}

.shield.shield-purple .status {
  background: #923293;
}

.shield.shield-red .status {
  background: #cc5640;
}

.shield.shield-blue .status {
  background: #0e7fbf;
}

.shield.shield-info .status {
  background: #5BC0DE;
}

.shield.shield-time .status {
  background: #7f4bca;
}
