"},"$:/plugins/oeyoews/neotw/ios-zoom-prevent":{"title":"$:/plugins/oeyoews/neotw/ios-zoom-prevent","tags":"$:/tags/RawMarkup","type":"application/javascript","text":"<script>
window.onload = function() {
    // 阻止双击放大
    var lastTouchEnd = 0;
    document.addEventListener('touchstart', function(event) {
        if (event.touches.length > 1) {
            event.preventDefault();
        }
    });
    document.addEventListener('touchend', function(event) {
        var now = (new Date()).getTime();
        if (now - lastTouchEnd <= 300) {
            event.preventDefault();
        }
        lastTouchEnd = now;
    }, false);

    // 阻止双指放大
    document.addEventListener('gesturestart', function(event) {
        event.preventDefault();
    });
}
</script>
"},"$:/plugins/oeyoews/neotw/style/user-panel-avatar":{"title":"$:/plugins/oeyoews/neotw/style/user-panel-avatar","tags":"$:/tags/Stylesheet","text":".user-panel-avatar { position: relative; max-width: 22px; max-height: 22px; }

