const{widget:e}=require("$:/core/modules/widgets/widget.js");exports.fullscreen=class extends e{constructor(e,t){super(e,t)}render(e,t){if(!$tw.browser)return;this.computeAttributes(),this.execute();const r=$tw.utils.domMaker,s=this.getVariable("currentTiddler"),l=r("button",{text:"ToggleFullscreen"});l.addEventListener("click",(()=>{null===document.fullscreenElement?document.querySelector(`[data-tiddler-title="${s}"]`).requestFullscreen():document.exitFullscreen()}));const n=r("div",{children:[l]});e.insertBefore(n,t),this.domNodes.push(n)}};","type":"application/javascript","module-type":"widget"}}}
