"},"$:/plugins/oeyoews/neotw-emoji/macro":{"title":"$:/plugins/oeyoews/neotw-emoji/macro","tags":"$:/tags/Macro","text":"\whitespace trim

\define emoji(symbol:rocket)
	<span class="neotw-$symbol$"></span>
\end

<!-- old -->
\define nw-gradient(text)
	<span class="nw-gradient">$text$</span>
\end

<!-- new -->
\define emojit(text)
	<span class="nw-gradient">$text$</span>
\end

\define emojig(symbol:rocket)
	<span class="neotw-$symbol$ nw-gradient"></span>
\end
"},"$:/plugins/oeyoews/neotw-emoji/style/bullet":{"title":"$:/plugins/oeyoews/neotw-emoji/style/bullet","tags":"$:/tags/Stylesheet","text":"\define dict(text)
		content: "{{$:/plugins/oeyoews/neotw-emoji/dict##$text$}} ";
\end

ul.bullets li::marker {
		color: #7d7dfc;
}

ul.bullets>li::marker {
	<<dict b1>>
}

ul.bullets>li>ul>li::marker {
		<<dict b2>>
		color: #9ECE6A;
}

ul.bullets>li>ul>li>ul>li::marker {
		<<dict b3>>
		color: #7DCFFF;
}

ul.bullets>li>ul>li>ul>li>ul li::marker {
		<<dict b4>>
		color: #2AC3DE;
}
"},"$:/plugins/oeyoews/neotw-emoji/gradient":{"title":"$:/plugins/oeyoews/neotw-emoji/gradient","tags":"$:/tags/Stylesheet","text":"<!-- old compatiable -->
.neotw-gradient {
	background: linear-gradient(45deg, #12c2e9, #c471ed, #f64f59);
	-webkit-background-clip: text;
	color: transparent;
}

<!-- new -->
.nw-gradient {
	background: linear-gradient(45deg, #12c2e9, #c471ed, #f64f59);
	-webkit-background-clip: text;
	color: transparent;
}
