.neotw-gradient {
	background: linear-gradient(45deg, #12c2e9, #c471ed, #f64f59);
	-webkit-background-clip: text;
	color: transparent;
}

<!-- new -->
.nw-gradient {
	background: linear-gradient(45deg, #12c2e9, #c471ed, #f64f59);
	-webkit-background-clip: text;
	color: transparent;
}
