"},"$:/plugins/oeyoews/neotw-hitokoto/hitokoto-js":{"title":"$:/plugins/oeyoews/neotw-hitokoto/hitokoto-js","text":"function hitokoto_footer(text = '#htext', author = '#hauthor') {
  fetch('https://v1.hitokoto.cn')
    .then(response => response.json())
    .then(data => {
      const hitokoto = document.querySelector(text);
      let data_ = data.hitokoto;
      // if(data_.length > 28){
      // data_ = data_.substring(0,28);
      // data_ = data_ + "…";
      // }
      hitokoto.innerText = data_;
      const hitokoto_from = document.querySelector(author);
      // hitokoto_from.href = "https://hitokoto.cn/?uuid=" + data.uuid;
      hitokoto_from.innerText = '@' + data.from;
    })
    .catch(console.error);
}

const htext_example = '#htext-example';
const hauthor_example = '#hauthor-example';

hitokoto_footer();
setInterval('hitokoto_footer()', 30000);

hitokoto_footer(htext_example, hauthor_example);
setInterval('hitokoto_footer(htext_example, hauthor_example)', 3000);
"},"$:/plugins/oeyoews/neotw-hitokoto/icon":{"title":"$:/plugins/oeyoews/neotw-hitokoto/icon","text":"<svg xmlns="http://www.w3.org/2000/svg" width="42.667" height="42.667" stroke-width="0" viewBox="0 0 32 32"><path fill="#8921ff" d="M9.3 4.8 3 8.4v15.2l6.5 3.7 6.6 3.8 3.2-1.8c1.7-1 3.7-2.3 4.4-3 .7-.6 2.2-1.5 3.3-1.8 1.8-.6 2-1.5 2-8.5V8.2l-6.4-3.6C19.1 2.6 16 1 15.8 1c-.1.1-3.1 1.7-6.5 3.8zm3.7 6.7c0 2.9-.2 2.9 4.4 1 3-1.3 3.6-1.3 3.6-.1 0 .7-1 1.7-2.2 2.1-1.3.4-2.6.8-3 1-.5.1-1.2.3-1.8.4-.5 0-1 1.2-1 2.4 0 2.2.3 2.3 4.5 2 4.4-.4 6.1.9 2.9 2.1-2.7 1-8.2.7-9.1-.7-.4-.6-.8-3.8-.9-7 0-4.1.3-5.7 1.3-5.7.7 0 1.3 1.1 1.3 2.5z"/></svg>
