<$list filter="[{$:/layout}match[$:/core/ui/PageTemplate]]">
footer {
  text-align: center;
  flex-wrap: wrap;
	flex: 1 1 auto;
	display:flex !important;
	justify-content:center;
	align-items: center;
	z-index:-1;
	color: gray;
	user-select: none;
	padding: 2px;
	font-size: 50%;
}

footer .inline-icon svg{
  height:1em;
}

#hitokoto,
#h-author {
  background: linear-gradient(45deg, #12c2e9, #c471ed, #f64f59);
  -webkit-background-clip: text;
  color: transparent;
}

#h-cursor:hover {
  cursor: pointer;
}

.hexample {
    font-size: 50%;
}

</$list>

<$list filter="[{$:/layout}!match[$:/core/ui/PageTemplate]]">
footer {
    display: none !important;
}
</$list>
