(function () {
  /*jslint node: true, browser: true */
  /*global $tw: false */
  'use strict';

  exports.name = 'notify-startup-hook';
  exports.platforms = ['browser'];
  exports.after = ['startup'];
  exports.synchronous = true;
  exports.startup = () => {
    const defaultOptions = {};
    window.pushNotify = (
      status = 'success',
      title = 'Notify',
      text = 'Text',
      effect = 'slide',
      autoclose = true,
    ) =>
      new Notify({
        status,
        title,
        text,
        effect,
        speed: 300,
        autoclose,
        customClass: null,
        customIcon: null,
        showIcon: true,
        showCloseButton: true,
        autotimeout: 3000,
        gap: 20,
        distance: 20,
        type: 1,
        // option
        position: 'right top',
      });
    $tw.rootWidget.addEventListener('om-notify', event => {
      try {
        const paramObject = event.paramObject || {};
        const {
          status = 'success',
          title = 'Notify',
          text = 'Text',
          autoclose = true,
        } = paramObject;
        pushNotify(status, title, text, autoclose);
      } catch {}
    });
  };
})();
